import './App.css';
import { BrowserRouter, Routes, Route } from 'react-router-dom';
import Navbar from './components/Navbar/Navbar';
import { TextSlide } from './components/TextSlide/TextSlide';
import { ImageCarousel } from './components/ImageCarousel/ImageCarousel';
import SportsCategorySection from './components/SportsCategorySection/SportsCategorySection';
import LoginPage from './pages/LoginPage';
import React, { useState } from 'react';

function App() {
  const [isLoginOpen, setIsLoginOpen] = useState(false);

  const openLoginModal = () => setIsLoginOpen(true);
  const closeLoginModal = () => setIsLoginOpen(false);

  return (
    <div className={isLoginOpen ? 'blurred' : ''}>
      <BrowserRouter>
        <Navbar openLoginModal={openLoginModal}></Navbar>
        <TextSlide></TextSlide>
        <ImageCarousel></ImageCarousel>
        <SportsCategorySection></SportsCategorySection>

        <Routes>
          <Route path='/login' element={<LoginPage />} />
        </Routes>

        {isLoginOpen && <LoginPage onClose={closeLoginModal} />}
      </BrowserRouter>
    </div>
  );
}

export default App;
