import React from 'react';
import './Item.css';

export const Item = (props) => {
  // Determine badge color based on rating
  let badgeClass = '';
  if (props.rating > 3.5) {
    badgeClass = 'green-badge';
  } else if (props.rating >= 3 && props.rating <= 3.5) {
    badgeClass = 'yellow-badge';
  } else {
    badgeClass = 'red-badge';
  }

  return (
    <div className='item'>
      <div className='item-image-container'>
        <img src={props.image} alt={props.name} />
      </div>
      <div className='item-details'>
        <p className='item-name'>{props.name}</p>

        {/* Rating Section */}
        <div className="item-rating">
          <span className={`rating-badge ${badgeClass}`}>{props.rating}</span>
          <span className="rating-count">({props.numRatings} ratings)</span>
        </div>

        <div className='item-prices'>
          <div className='item-prices-new'>₹{props.new_price}</div>
          <div className='item-prices-old'>₹{props.old_price}</div>
        </div>
      </div>
    </div>
  );
};
